const items = $input.all();
if (items.length === 0) {
    return [];
}

const data = items[0].json;

// Ensure "attempt" is set; default to 0 if missing
const attempt = data.attempt !== undefined ? data.attempt : 0;

// The "slots" object might have multiple date keys. We only pick the first one.
const dateKeys = Object.keys(data.slots || {});
if (dateKeys.length === 0) {
  // No date keys => no slots at all, so return with empty availability
  return [{
    json: {
      day: null,
      availSlots: [],
      startTime: data.startTime,
      attempt: attempt
    }
  }];
}

const dateKey = dateKeys[0]; // First available date
const timeSlots = data.slots[dateKey] || [];

// Shuffle function
const shuffleArray = (arr) => arr.sort(() => Math.random() - 0.5);
const shuffled = shuffleArray([...timeSlots]);

// Slice first 3 slots
const sliced = shuffled.slice(0, 3);

// Sort by time (assuming each slot has a "time" property)
sliced.sort((a, b) => new Date(a.time) - new Date(b.time));

// Return the processed data along with day, startTime, and attempt
return [{
  json: {
    day: dateKey,
    availSlots: sliced,
    startTime: data.startTime,
    attempt: attempt
  }
}];

