const data = $input.first().json;
const availSlots = data.availSlots || [];
const attempt = data.attempt !== undefined ? Number(data.attempt) : 0;
const startTime = data.startTime;

// If availSlots isn’t an array or is empty, return "busy"
if (!Array.isArray(availSlots) || availSlots.length === 0) {
  return [{
    json: {
      availSlots: "busy",
      attempt,
      startTime
    }
  }];
}

function formatTime(dateInput) {
  const d = new Date(dateInput);
  const options = { 
    hour: 'numeric', 
    minute: 'numeric', 
    hour12: true, 
    timeZone: 'Asia/Kolkata' // Changed from America/New_York to Asia/Kolkata
  };
  return d.toLocaleTimeString('en-IN', options); // Optional: 'en-IN' for Indian-style formatting
}

// Filter out any slots without a valid "time" property
const validSlots = availSlots.filter(slot => slot.time);
if (validSlots.length === 0) {
  return [{
    json: {
      availSlots: "busy",
      attempt,
      startTime
    }
  }];
}

// Format each valid slot's time and join them with commas
const formatted = validSlots
  .map(slot => formatTime(slot.time))
  .join(', ');

// If the formatted string is empty, return "busy"
if (!formatted) {
  return [{
    json: {
      availSlots: "busy",
      attempt,
      startTime
    }
  }];
}

return [{
