function formatSize(bytes) {
  if (!bytes) return '';
  const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
  const i = Math.floor(Math.log(bytes) / Math.log(1024));
  return (bytes / Math.pow(1024, i)).toFixed(1) + ' ' + sizes[i];
}
function isFolder(f) {
  return f.mimeType === 'application/vnd.google-apps.folder';
}
function formatDate(iso) {
  if (!iso) return '';
  const d = new Date(iso);
  return d.toLocaleString('en-US', { dateStyle: 'short', timeStyle: 'short' });
}

// Build lookup map
const map = new Map();
files.forEach(f => map.set(f.id, { ...f, children: [] }));

// Attach children to parents
for (const f of map.values()) {
  if (f.parents && f.parents.length) {
    const parentId = f.parents[0];
    if (map.has(parentId)) {
      map.get(parentId).children.push(f);
    }
  }
}

// Find roots (items with no known parent)
const roots = [...map.values()].filter(f => !f.parents || !map.has(f.parents[0]));

// Recursive renderer
function render(node, depth = 0) {
  const indent = ' '.repeat(depth * 4);
  const icon = isFolder(node) ? 'üìÅ' : 'üìÑ';
  const size = isFolder(node) ? '' : ` (${formatSize(Number(node.size))})`;
  const owner = node.owners && node.owners.length ? ` ‚Äî üë§ ${node.owners[0].displayName}` : '';
  const created = node.createdTime ? ` ‚Äî üïí ${formatDate(node.createdTime)}` : '';
  let out = `${indent}- ${icon} **${node.name}**${size}${owner}${created}\n`;
  for (const child of node.children.sort((a, b) => {
    const af = isFolder(a), bf = isFolder(b);
    if (af !== bf) return af ? -1 : 1;
    return a.name.localeCompare(b.name);
  })) {
    out += render(child, depth + 1);
  }
  return out;
}

// Generate markdown
let tree = '# üìÇ Directory Structure\n\n';
for (const root of roots) {
  tree += render(root);
