// Run Once for All Items

const md = $input.first()?.json?.markdown ?? '';
const lines = String(md).split('\n');

// Unicode-aware regex; supports bold/non-bold and ignores trailing metadata
const FOLDER_RE = /^(\s*)-\s*üìÅ\s+(?:\*\*)?([^*].*?)(?:\*\*)?\s*(?:‚Äî.*)?$/u;

const folders = [];

for (const raw of lines) {
  const line = String(raw || '');
  const m = line.match(FOLDER_RE);
  if (!m) continue;

  const indent = m[1] ?? '';
  const nameRaw = m[2] ?? '';
  const depth = Math.floor(indent.length / 2);
  folders.push({ json: { nameSeen: nameRaw, depth, line } });
}

// If nothing parsed as folders, emit diagnostics to adjust regex
if (folders.length === 0) {
  return [{
    json: {
      ok: false,
      reason: 'No folder lines matched. The emoji, dash, or spacing likely differ.',
      sampleFirstLines: lines.slice(0, 10),
      tip: 'Confirm the folder bullet uses "- [space] üìÅ [space]". If bullets use a different dash/emoji or tabs, adjust the regex.',
    }
  }];
}

return folders;

// Run Once for All Items

// Paste the exact value observed in the previous step, e.g. "Company Informations"
const targetFolder = 'GMI Document & PPT Template';

const md = $input.first()?.json?.markdown ?? '';
const lines = String(md).split('\n');

const FOLDER_RE = /^(\s*)-\s*üìÅ\s+(?:\*\*)?([^*].*?)(?:\*\*)?\s*(?:‚Äî.*)?$/u;
const FILE_RE   = /^(\s*)-\s*üìÑ\s+/u;

// 1) Find the exact folder line
let startIndex = -1;
let baseIndentLen = 0;

for (let i = 0; i < lines.length; i++) {
  const line = String(lines[i] || '');
  const m = line.match(FOLDER_RE);
  if (!m) continue;

  const rawName = m[3] ?? '';
  if (rawName === targetFolder) {
    startIndex = i;
    baseIndentLen = (m[4] ?? '').length;
    break;
  }
}

if (startIndex === -1) {
  return [{
    json: {
      ok: false,
      reason: 'Target folder not found with exact match. Use the ‚ÄúList detected folder names‚Äù node to copy nameSeen exactly.',
      triedTarget: targetFolder,
    },
  }];
}

// 2) Collect subtree until a sibling/ancestor (indent <= base) line appears
const subtree = [];
subtree.push(lines[startIndex]);

for (let i = startIndex + 1; i < lines.length; i++) {
  const line = String(lines[i] || '');
  const indentMatch = line.match(/^(\s*)/);
  const indentLen = indentMatch ? indentMatch[4].length : 0;
  const isFolder = FOLDER_RE.test(line);
  const isFile = FILE_RE.test(line);

  if ((isFolder || isFile) && indentLen <= baseIndentLen) break;
  subtree.push(line);
}

return [{
  json: {
    ok: true,
    folder: targetFolder,
    markdown: subtree.join('\n'),
  },
}];
